import { useDispatch } from 'react-redux'
import { deleteGoal } from '../components/features/goals/goalSlice'

function GoalItem({ goal }) {
  const dispatch = useDispatch()
  

  const dateConverter = (startDate, timeEnd) => {
    const newStartDate= new Date(goal.createdAt);
    const newEndDate=new Date(timeEnd);
    const one_day = 1000*60*60*24;
    let result
    result = Math.ceil((newEndDate.getTime()-newStartDate.getTime())/(one_day))
    console.log('date Converter result', result)
    if (result < 0 ) {return 0}
    return result
  }


  return (
    <div className='goal'>
      <div>{new Date(goal.createdAt).toLocaleString('en-US')}</div>
      <div>{dateConverter.text}</div>
      <h2>Habit: {goal.text}</h2>
      <h2>{goal.days} Days</h2>
      <button onClick={() => dispatch(deleteGoal(goal._id))} className='close'>
        {/* Placeholder for green tick */} X
      </button>
    </div>
  )
}





export default GoalItem
