import { useDispatch } from 'react-redux'
import { deleteGoal } from '../components/features/goals/goalSlice'
// import{ ExpiredNotice, ShowCounter, CountdownTimer } from '../hooks/CountdownTimer'
import React from 'react';
import { useCountdown } from '../hooks/useCountdown';
import DateTimeDisplay from '../hooks/DateTimeDisplay';

function GoalItem({ goal }) {
  const dispatch = useDispatch()

  return (
    <div className='goal'>
      <div>{new Date(goal.createdAt).toLocaleString('en-US')}</div>
      <h2>Habit: {goal.text}</h2>
      <h2>{goal.days} Days</h2>
      <button onClick={() => dispatch(deleteGoal(goal._id))} className='close'>
        {/* Placeholder for green tick */} X
      </button>
    </div>
  )
}
const ExpiredNotice = () => {
  return (
    <div className="expired-notice">
      <span>Expired!!!</span>
      <p>Please select a future date and time.</p>
    </div>
  );
};

const ShowCounter = ({ days, hours, minutes, seconds }) => {
  return (
    <div className="show-counter">
      <a
        href="https://tapasadhikary.com"
        target="_blank"
        rel="noopener noreferrer"
        className="countdown-link"
      >
        <DateTimeDisplay value={days} type={'Days'} isDanger={days <= 3} />
        <p>:</p>
        <DateTimeDisplay value={hours} type={'Hours'} isDanger={false} />
        <p>:</p>
        <DateTimeDisplay value={minutes} type={'Mins'} isDanger={false} />
        <p>:</p>
        <DateTimeDisplay value={seconds} type={'Seconds'} isDanger={false} />
      </a>
    </div>
  );
};

const CountdownTimer = ({ targetDate }) => {
const [days, hours, minutes, seconds] = useCountdown(targetDate);

if (days + hours + minutes + seconds <= 0) {
  return <ExpiredNotice />;
} else {
  return (
    <ShowCounter
      days={days}
      hours={hours}
      minutes={minutes}
      seconds={seconds}
    />
  );
}
};

export default GoalItem
