import { useState } from 'react'
import { useDispatch } from 'react-redux'
import { createGoal } from '../components/features/goals/goalSlice'

function GoalForm() {
  const [text, setText] = useState('')
  const [days, setNumber] = useState('')

  const dispatch = useDispatch()

  const onSubmit = (e) => {
    e.preventDefault()

    dispatch(createGoal({ text, days }))
    setText('')
    setNumber('')
  }

  return (
    <section className='form'>
      <form onSubmit={onSubmit}>
        <div className='form-group'>
          <label htmlFor='text'>Create a Habit you would like to track</label>
          <input
            type='text'
            name='text'
            id='text'
            placeholder='What habit would you like to track?'
            value={text}
            onChange={(e) => setText(e.target.value)}
          />
              <input 
            placeholder='Over how many days'
            id='days'
            type='days'
            name='days'
            value={days} 
            onChange={(e) => setNumber(e.target.value)}
          />
        </div>
        <div className='form-group'>
          <button className='btn btn-block' type='submit'>
            Add Habit
          </button>
        </div>
      </form>
    </section>
    
  )
}
function App() {
  const calculateTimeLeft = () => {
    let year = new Date().getFullYear();
    const difference = +new Date(`${year}-10-1`) - +new Date();
    let timeLeft = {};

    if (difference > 0) {
      timeLeft = {
        days: Math.floor(difference / (1000 * 60 * 60 * 24)),
        hours: Math.floor((difference / (1000 * 60 * 60)) % 24),
        minutes: Math.floor((difference / 1000 / 60) % 60),
        seconds: Math.floor((difference / 1000) % 60),
      };
    }

    return timeLeft;
  };

  const [timeLeft, setTimeLeft] = useState(calculateTimeLeft());
  const [year] = useState(new Date().getFullYear());

  useEffect(() => {
    setTimeout(() => {
      setTimeLeft(calculateTimeLeft());
    }, 1000);
  });

  const timerComponents = [];

  Object.keys(timeLeft).forEach((interval) => {
    if (!timeLeft[interval]) {
      return;
    }

    timerComponents.push(
      <span>
        {timeLeft[interval]} {interval}{" "}
      </span>
    );
  });
  return (
    <div>
      <h1>HacktoberFest {year} Countdown</h1>
      <h2>With React Hooks!</h2>
      {timerComponents.length ? timerComponents : <span>Time's up!</span>}
    </div>
  );
}

// eslint-disable-next-line import/no-anonymous-default-export
export default {
  GoalForm,
  App
}
